SELECT cno, title, intro, poster, induty, lctcl, addr,doNm, num FROM ( SELECT cno, title, intro, poster, induty, lctcl, addr,doNm, rownum AS num FROM ( SELECT /*+ INDEX_ASC(camp camp_no_pk) */ cno, title, intro, poster, induty, lctcl, addr,doNm FROM camp WHERE poster IS NOT NULL
AND (
doNm LIKE '%' || #{loc} || '%'
)
) ) WHERE num BETWEEN #{start} AND #{end}
